  };

 console.log('the value of val is',val)

 const deep=val.filter((x)=>{return x.home_port='Port Canaveral'})
 console.log('the value of deep is',deep);


  useEffect(() => {
    fetchShips();
  },[]);

  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <input type="text"
      placeholder="input the ship name"
      onChange={(e)=>{setSearch(e.target.value)}}
      />

    </div>
  );
}
