import React, { useEffect, useState } from "react";
import "./styles.css";
import gql from "graphql-tag";
import request from "./utils/request";
import Test from "./Test";

export default function App() {
  const[val,setVal]=useState([]);
  const[search,setSearch]=useState("");


  const fetchShips = async () => {
    const response = await request(gql`
      {
        ships {
          name
          home_port
          image
        }
      }
    `);

    setVal(response.data.ships);

  };

  const filterfun=function(item)
  {
    const temp=val.filter((x)=>{return x.home_port===item})
    setVal(temp)
  }






  useEffect(() => {
    fetchShips();
  },[]);

  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <input type="text"
      placeholder="input the ship name"
      onChange={(e)=>{setSearch(e.target.value)}}
      />
      <button type="submit" onClick={()=>{filterfun(search)}}>search</button>

     { val.map((x)=>{
    return <h1>{x.home_port}</h1>
  })}

    </div>
  );
}
